mapper.select:
SELECT panel_group.id, panel_group.`name`, panel_group.pid, panel_group.`level`, panel_group.node_type, panel_group.create_by, panel_group.create_time, panel_group.panel_type, panel_group.`name` AS label, panel_group.`source`, panel_group.`panel_type`, sourcePanelGroup.`name` as source_panel_name, get_auths(panel_group.id,'panel',#{userId}) as `privileges` from (select GET_V_AUTH_MODEL_ID_P_USE (#{userId}, 'panel') cids) t,panel_group LEFT JOIN (select id,`name` from panel_group) sourcePanelGroup on sourcePanelGroup.id=panel_group.source
FIND_IN_SET(panel_group.id,cids)
and panel_group.name like CONCAT('%', #{name},'%')
and panel_group.node_type = #{nodeType}
and panel_group.panel_type = #{panelType}
and panel_group.id = #{id}
and panel_group.pid = #{pid}
and panel_group.level = #{level}
order by ${sort}
order by panel_group.create_time desc

SELECT panel_group.id, panel_group.`name`, panel_group.pid, panel_group.`level`, panel_group.node_type, panel_group.create_by, panel_group.create_time, panel_group.panel_type, panel_group.`name` AS label, panel_group.`node_type`, (case when ISNULL(defaultPanelGroup.id) then false else true end) is_default, defaultPanelGroup.id as default_panel_id, defaultPanelGroup.`name` as default_panel_name, get_auths(panel_group.id,'panel',#{userId}) as `privileges` from (select GET_V_AUTH_MODEL_ID_P_USE (#{userId}, 'panel') cids) t,panel_group LEFT JOIN (select source,id,`name` from panel_group where source is not null) defaultPanelGroup on defaultPanelGroup.source=panel_group.id
FIND_IN_SET(panel_group.id,cids)
and panel_group.name like CONCAT('%', #{name},'%')
and panel_group.node_type = #{nodeType}
and panel_group.panel_type = #{panelType}
and panel_group.id = #{id}
and panel_group.pid = #{pid}
and panel_group.level = #{level}
order by ${sort}
order by panel_group.create_time desc
