mapper.select:
SELECT auth.*, authCount.children_count AS children_count, IF (( authCount.children_count > 0 ), 0, 1 ) AS leaf FROM (select get_grant_auths (#{modelType},#{createBy}) c_auth_ids) t1, ( SELECT * FROM (select GET_V_AUTH_MODEL_WITH_PARENT(get_grant_auths (#{modelType},#{createBy}),#{modelType}) c_auth_parent_ids) t2,
(select GET_V_AUTH_MODEL_WITH_PARENT(#{id},#{modelType}) c_model_parent_ids) tmp,
(select GET_V_AUTH_MODEL_WITH_CHILDREN(#{id},#{modelType}) c_model_children_ids) tmc,
(select GET_V_AUTH_MODEL_WITH_PARENT ( (select GROUP_CONCAT(id) from v_auth_model where model_type = #{modelType} and `name` like CONCAT('%', #{name},'%')) ,#{modelType}) c_model_parent_seartch_ids) tmsc,
v_auth_model
model_type = #{modelType}
and FIND_IN_SET(v_auth_model.id,c_auth_parent_ids)
and v_auth_model.pid = #{pid}
and v_auth_model.id = #{id}
and FIND_IN_SET(v_auth_model.id,c_model_parent_ids)
and FIND_IN_SET(v_auth_model.id,c_model_children_ids)
and FIND_IN_SET(v_auth_model.id,c_model_parent_seartch_ids)
and v_auth_model.pid = '0'
) auth LEFT JOIN ( SELECT count( 1 ) AS `children_count`, `authTemp`.`pid` AS `pid` FROM ( SELECT * FROM (select GET_V_AUTH_MODEL_WITH_PARENT(get_grant_auths (#{modelType},#{createBy}),#{modelType}) cids3) t3,v_auth_model
model_type = #{modelType}
and FIND_IN_SET(v_auth_model.id,cids3)
) authTemp GROUP BY authTemp.pid ) authCount ON auth.id = authCount.pid
(authCount.children_count>0 or FIND_IN_SET(auth.id,c_auth_ids) )
